import logo from './logo.svg';
import './App.css';
import CustomButton from './component/CustomButton';
import { useState } from 'react';

//데이터 생성시 보통 const
const ironMan = {
  name: 'Tony Stark',
  heroName: 'Iron-man',
  imgUrl: 'https://img.danawa.com/prod_img/500000/207/533/img/18533207_1.jpg?_v=20221226163359',
  imgSize: 100,
}

const weapons = [
  {title: 'Replusor Beam', idx:1},
  {title: 'Unibeam Blaster', idx:2},
  {title: 'Smart missile', idx:3},
]

const listWeapons = weapons.map(weapon =>
  <li key={weapon.idx}>
    {weapon.title}
  </li>
);

function IncButton() {
  const [count, setCount] = useState(0); //count = 변수,setCount = 변수값을 조정할 함수,useState(0) 최초 0 초기화

  function upClick() {
    setCount(count-1);
  }

  return (
    <button onClick={upClick}>
      {count}번 증가!!
    </button>
  )
}

function App() {
  return (
    <div className="App">
      <header className="App-header">   
        <h1>{ironMan.heroName}</h1>
        <img className ='profile'
              src= {ironMan.imgUrl}
              alt= {ironMan.name + " 변신사진"}
              style={{
                width: ironMan.imgSize,
                height: ironMan.imgSize,
              }}
        />
        <IncButton />
        <IncButton></IncButton>
        {/* <ul>{listWeapons}</ul>
        <CustomButton data={ironMan}/> */}
      </header>
    </div>
  );
}

export default App;
