import logo from './logo.svg';
import './App.css';
import CustomButton from './component/CustomButton';
import weapons from './component/Weapons'; 

//데이터 생성시 보통 const
const ironMan = {
  name: 'Tony Stark',
  heroName: 'Iron-man',
  imgUrl: 'https://img.danawa.com/prod_img/500000/207/533/img/18533207_1.jpg?_v=20221226163359',
  imgSize: 100,
}

const listWeapons = weapons.map(weapons =>
  <li key={weapons.idx}>
  {weapons.title}
  </li>
)

function App() {
  return (
    <div className="App">
      <header className="App-header">   
        <h1>{ironMan.heroName}</h1>
        <img className ='profile'
              src= {ironMan.imgUrl}
              alt= {ironMan.name + " 변신사진"}
              style={{
                width: ironMan.imgSize,
                height: ironMan.imgSize,
              }}
        />
        <CustomButton />
      </header>
    </div>
  );
}

export default App;
