function IncButton() {
    const [count, setCount] = useState(0); //count = 변수,setCount = 변수값을 조정할 함수,useState(0) 최초 0 초기화
  
    function upClick() {
      setCount(count+1);
    }
  
    return (
      <button onClick={upClick}>
        {count}번 증가!!
      </button>
    )
  }
